}
 
.header-1{
  text-align: -webkit-center;
}
  .header-2 .invoice-num{
    font-size: 12px;
    font-weight: bold;
    text-align: center;
  }
  .header-1 .invoice-heading{
    font-size: 1.5em;
    margin-left: 0;
    margin-right: 0;
    font-weight: bold;
}
  table{
      width: 100%;
   }
  .hed-right{
      border: 1px solid;
      width: fit-content;
      padding: 8px;
  }
  .table-header-right{
  
      /* margin-right: 22%; */
      /* margin-top: -27%; */
      width: 100%;
      text-align: center;
  }
  .data-container{
   color:red;   
  }
  
  
  .wrapper .table-header-right{
    /*margin: auto;*/
    padding: 20px;
    border-collapse: separate;
    border-spacing: 0;
  }
  thead tr>th,table tbody tr>td{
    border: 1px solid #000;
  }
  .wrapper .table-header-right td{
    border: 1px solid #000;
    
    
  }
  
  .wrapper .table-header-right tr:first-child td:first-child{
    border-top-right-radius: 10px;
  }
  .wrapper .table-header-right tr:last-child td:first-child{
    border-bottom-right-radius: 10px;
  }
  .wrapper .table-header-right tr:first-child td:last-child{
    border-top-left-radius: 10px;
  }
  .wrapper .table-header-right tr:last-child td:last-child{
    border-bottom-left-radius: 10px;
  }
  
  .row {
  margin-left: 0px;
      margin-right: 45px;
      margin-bottom: 10px;
    }
      
    .column {
      float: left;
      width: 50%;
    }
    
    
    .col-3 {
      float: left;
      width: 30%;
    }
    .col-9 {
      float: left;
      width: 70%;
    }
    
    /* Clearfix (clear floats) */
    .row::after {
      content: "";
      clear: both;
      display: table;
    }
    
    
    
    table tr>td , table tr>th{
      border: 1px solid #000;
    }
    tr > th {
      background-color: #ebebeb;
    }
    .print-format td, .print-format th {
      padding: 5px !important;
    }
     
    .table-totlal-invoice tr>th{
      background-color: #ebebeb;
      border: 1px solid #000;

    }
    .table-totlal-invoice tr>td {
      border: 1px solid #000;

    }
.ltr{
    text-align: left;}

    .table-totlal-invoice  tr>td:first-child{
      color: #b30000;
    }
    
    .print-format .table-bordered td, .print-format .table-bordered th,.table-bordered thead tr {
